import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useState } from 'react';
import Axios from 'axios';

function App() {

  const [nombre, setNombre] = useState("");
  const [edad, setEdad] = useState(0);
  const [pais, setPais] = useState("");
  const [cargo, setCargo] = useState("");
  const [anios, setAnios] = useState(0);

  const [empleados, setEmpleados] = useState([]);

  const add = () => {
    Axios.post("http://localhost:3001/create", {
      nombre: nombre,
      edad: edad,
      pais: pais,
      cargo: cargo,
      anios: anios
    }).then(() => {
      alert("Empleado registrado");
      getEmpleados();
    });
  }

  const getEmpleados = () => {
    Axios.get("http://localhost:3001/empleados").then((response) => {
      setEmpleados(response.data);
    });
  }

  return (
    <div className='App'>
      <div className="datos container">



        <div className='Lista'>
          <button onClick={getEmpleados} type="button" className="btn btn-primary">Listar</button>
          <h1>Empleados:</h1>

          {empleados.map((empleado) => (
            <div> {empleado.nombre} </div>
          ))}
        </div>


        <div className='card border-success mb-3" style={{ maxWidth: "18rem" }}'>
          <div className="card-header bg-transparent border-success">EMPLEADOS</div>
          <div className="card-body text-success">


          </div>
          <div className="card-footer bg-transparent border-success">
            <div>
              <button onClick={add} type="button" className="btn btn-success">REGISTRAR</button>
            </div>
          </div>
        </div>

      </div>
    </div>
  );
}

export default App;
