import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useState, useEffect} from 'react';
import Axios from 'axios';

function App() {

  const [nombre, setNombre] = useState("");
  const [edad, setEdad] = useState();
  const [pais, setPais] = useState("");
  const [cargo, setCargo] = useState("");
  const [anios, setAnios] = useState();
  const [id, setId] = useState();
  const [editar, setEditar] = useState(false);

  const [empleados, setEmpleados] = useState([]);

  const add = () => {
    Axios.post("http://localhost:3001/create", {
      nombre: nombre,
      edad: edad,
      pais: pais,
      cargo: cargo,
      anios: anios
    }).then(() => {
      alert("Empleado registrado");
      getEmpleados();
    });
  }

  const editarEmpleado = (val) => {
    setEditar(true);
    setId(val.id);
    setNombre(val.nombre);
    setEdad(val.edad);
    setPais(val.pais);
    setCargo(val.cargo);
    setAnios(val.anios);
  }

  const update = () => {
    Axios.put("http://localhost:3001/update", {
      id: id,
      nombre: nombre,
      edad: edad,
      pais: pais,
      cargo: cargo,
      anios: anios
    }).then(() => {
      getEmpleados();
    });
  }

  const limpiarDatos = () => {
    setEditar(false);
    setNombre();
    setEdad();
    setPais();
    setCargo("");
    setAnios("");
  }

  const getEmpleados = () => {
    Axios.get("http://localhost:3001/empleados").then((response) => {
      setEmpleados(response.data);
    });
  }

  useEffect(() => {
    getEmpleados(); // Llamar a getEmpleados cuando el componente se monta
  }, []); // El array vacÃ­o asegura que solo se ejecute una vez al montar el componente


  return (
    <div className="container">

      <div className='App'>
        <div className="datos">
          <div className="card text-center">
            <div className="card-header">GESTIÃN DE EMPLEADOS</div>
            <div className="card-body">
              <div className="input-group mb-3">
                <span className="input-group-text" id="basic-addon1">Nombre: </span>
                <input value={nombre}
                  onChange={(event) => setNombre(event.target.value)}
                  type="text" className="form-control" placeholder="Nombre" />
              </div>
              <div className="input-group mb-3">
                <span className="input-group-text" id="basic-addon1">Edad: </span>
                <input value={edad}
                  onChange={(event) => setEdad(event.target.value)}
                  type="number" className="form-control" placeholder="Edad" />
              </div>
              <div className="input-group mb-3">
                <span className="input-group-text" id="basic-addon1">PaÃ­: </span>
                <input value={pais}
                  onChange={(event) => setPais(event.target.value)}
                  type="text" className="form-control" placeholder="PaÃ­s" />
              </div>
              <div className="input-group mb-3">
                <span className="input-group-text" id="basic-addon1">Cargo: </span>
                <input value={cargo}
                  onChange={(event) => setCargo(event.target.value)}
                  type="text" className="form-control" placeholder="Cargo" />
              </div>
              <div className="input-group mb-3">
                <span className="input-group-text" id="basic-addon1">AÃ±os: </span>
                <input value={anios}
                  onChange={(event) => setAnios(event.target.value)}
                  type="number" className="form-control" placeholder="Experiencia" />
              </div>

            </div>
            <div className="card-footer text-muted">
              <div>
                {
                  editar ? <div>
                    <button onClick={update} type="button" className="btn btn-info ml-2">Actualizar</button>
                    <button onClick={add} type="button" className="btn btn-danger mr-2">Cancelar</button>
                  </div>
                    :
                    <button onClick={add} type="button" className="btn btn-success">REGISTRAR</button>
                }
              </div>
            </div>
          </div>
        </div>

        <table className="table table-striped">
          <thead>
            <tr>
              <th scope="col">#</th>
              <th scope="col">Nombre</th>
              <th scope="col">Edad</th>
              <th scope="col">PaÃ­s</th>
              <th scope="col">Cargo</th>
              <th scope="col">Experiencia</th>
              <th scope="col">Acciones</th>
            </tr>
          </thead>
          <tbody>
            {
              empleados.map((val, key) => {
                return <tr key={val.id}>
                  <th scope="row">{val.id}</th>
                  <td>{val.nombre}</td>
                  <td>{val.edad}</td>
                  <td>{val.pais}</td>
                  <td>{val.cargo}</td>
                  <td>{val.anios}</td>
                  <td>
                    <div className="btn-group" role="group" aria-label="Basic example">
                      <button
                        onClick={() => editarEmpleado(val)} type="button" className="btn btn-info">Editar</button>
                      <button

                        type="button" className="btn btn-danger">Eliminar</button>
                    </div>
                  </td>
                </tr>
              })
            }
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
