import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useState } from 'react';
import Axios from 'axios';

function App() {

  const [nombre, setNombre] = useState("");
  const [edad, setEdad] = useState(0);
  const [pais, setPais] = useState("");
  const [cargo, setCargo] = useState("");
  const [anios, setAnios] = useState(0);

  const [empleados, setEmpleados] = useState([]);

  const add = () => {
    Axios.post("http://localhost:3001/create", {
      nombre: nombre,
      edad: edad,
      pais: pais,
      cargo: cargo,
      anios: anios
    }).then(() => {
      alert("Empleado registrado");
    });
  }

  const getEmpleados = () => {
    Axios.get("http://localhost:3001/empleados").then((response) => {
      setEmpleados(response.data);
      if (Array.isArray(response.data)) {
        setEmpleados(response.data);
        console.log('Empleados actualizados:', response.data);
        alert("Empleados obtenidos");
      } else {
        console.error('La respuesta no es un array:', response.data);
      }
      alert("Empleados obtenidos");
    });
  }

  return (
    <div className='App'>
      <div className="datos container">
        <div className="input-group mb-3">
          <span className="input-group-text" id="basic-addon1">Nombre: </span>
          <input
            onChange={(event) => setNombre(event.target.value)}
            type="text" className="form-control" placeholder="Nombre" />
        </div>
        <div className="input-group mb-3">
          <span className="input-group-text" id="basic-addon1">Edad: </span>
          <input
            onChange={(event) => setEdad(event.target.value)}
            type="number" className="form-control" placeholder="Edad" />
        </div>
        <div className="input-group mb-3">
          <span className="input-group-text" id="basic-addon1">Paí: </span>
          <input
            onChange={(event) => setPais(event.target.value)}
            type="text" className="form-control" placeholder="País" />
        </div>
        <div className="input-group mb-3">
          <span className="input-group-text" id="basic-addon1">Cargo: </span>
          <input
            onChange={(event) => setCargo(event.target.value)}
            type="text" className="form-control" placeholder="Cargo" />
        </div>
        <div className="input-group mb-3">
          <span className="input-group-text" id="basic-addon1">Años: </span>
          <input
            onChange={(event) => setAnios(event.target.value)}
            type="number" className="form-control" placeholder="Experiencia" />
        </div>
        <div>
          <button onClick={add} type="button" className="btn btn-primary">REGISTRAR</button>
        </div>

        <div className='Lista'>
          <button onClick={getEmpleados} type="button" className="btn btn-primary">Listar</button>
          <h1>Empleados:</h1>
          {Array.isArray(empleados) && empleados.map((empleado) => (
            <div> ${empleado.nombre} </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
